import axios from "axios";
import Filter from "bad-words";

const BACKEND_API_URL = "lolocalhost:3000";

export const startHerokuVM = () => async dispatch => {
  await axios.get(); // ping the Heroku backend VM for starting it
};

export const fetchRiders = () => async dispatch => {
  try {
    const res = await axios.get(`${BACKEND_API_URL}/riders`);
    if (res.status === 200) {
      dispatch({ type: FETCH_RIDERS, riders: res.data });
    }
  } catch ({ response }) {
    new Error('backend api failed')
  }
};

export const fetchRider = id => async dispatch => {
    try {
      const res = await axios.get(`${BACKEND_API_URL}/riders/${id}`);
      dispatch({
        type: FETCH_RIDER,
        rider: res.data[0]
      });
    } catch ({ response }) {
        new Error('backend api failed')
    }
  };

export default {
  fetchRiders, startHerokuVM
};
