import React, { Component } from "react";
import { Card, Container, Row, Col, Button } from "react-bootstrap";
import "./Sponsoring.scss";

class Sponsoring extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      slogan: ""
    };
  }

  handleChangeName = elem => {
    this.setState({ name: elem.target.value });
  };

  handleChangeEmail = elem => {
    this.setState({ email: elem.target.value });
  };

  handleChangeSlogan = elem => {
    this.setState({ slogan: elem.target.value });
  };

  handleSubmit = () => {
    const { name, email, slogan } = this.state;
  }

  render() {
    return (
      <Container style={{ marginTop: "30px" }}>
        <Row>
          <Col xs={12}>
            <Card
              bg={"light"}
              style={{ display: "flex", justifyContent: "center" }}
            >
              <Card.Header>
                Wants to be sponsored?{" "}
                <span role={"img"} aria-label="smile">
                  ğŸ˜€
                </span>
              </Card.Header>
              <Card.Body>
                <div className="small-container">
                  <label
                    form="name"
                    style={{ textAlign: "left", fontWeight: "bold" }}
                  >
                    Name
                  </label>
                  <input
                    type="text"
                    name="name"
                    onChange={this.handleChangeName}
                  />
                  <label form="Email Address" onChange={this.handleChangeEmail}>
                    Email Address
                  </label>
                  <input type="email" name="name" />
                  <label form="Message">Your slogan for the race</label>
                  <textarea name="message" onChange={this.handleChangeSlogan} />
                  <Button variant="success" onClick={() => false}>
                    Submit
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Sponsoring;
