import React, { Component } from "react";
import { Card, Container, Row, Col, Button, Alert } from "react-bootstrap";
import "./Sponsoring.scss";
import { submitSponsorship } from "../api/backendApi.js";

class Sponsoring extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      slogan: "",
      isSubmit: false,
      alert: false
    };
  }

  handleChangeFirstName = elem => {
    this.setState({ firstName: elem.target.value });
  };

  handleChangeLastName = elem => {
    this.setState({ lastName: elem.target.value });
  };

  handleChangeEmail = elem => {
    this.setState({ email: elem.target.value });
  };

  handleChangeSlogan = elem => {
    this.setState({ slogan: elem.target.value });
  };

  handleSubmit = () => {
    if (
      !this.state.firstName ||
      !this.state.lastName ||
      !this.state.email ||
      !this.state.slogan
    )
      return this.setState({ alert: true });
    submitSponsorship({ ...this.state });
    this.setState({ isSubmit: true });
  };

  render() {
    return (
      <Container style={{ marginTop: "30px" }}>
        <Row>
          {this.state.isSubmit && (
            <Alert variant="success">
              <Alert.Heading>Your sponsorship is validated</Alert.Heading>
              <div className="d-flex justify-content-end">
                <Button
                  onClick={() => this.setState({ isSubmit: false })}
                  variant="outline-success"
                >
                  Close
                </Button>
              </div>
            </Alert>
          )}
          {this.state.alert && (
            <Alert variant="danger">
              <Alert.Heading>Inputs can not be empty</Alert.Heading>
              <div className="d-flex justify-content-end">
                <Button
                  onClick={() => this.setState({ alert: false })}
                  variant="outline-danger"
                >
                  Close
                </Button>
              </div>
            </Alert>
          )}
          <Col xs={12}>
            <Card
              bg={"light"}
              style={{ display: "flex", justifyContent: "center" }}
            >
              <Card.Header>
                Wants to be sponsored?{" "}
                <span role={"img"} aria-label="smile">
                  😀
                </span>
              </Card.Header>
              <Card.Body>
                <div className="small-container">
                  <label
                    form="text"
                    style={{ textAlign: "left", fontWeight: "bold" }}
                  >
                    First name
                  </label>
                  <input
                    type="text"
                    name="first name"
                    minLength={2}
                    maxLength={30}
                    onChange={this.handleChangeFirstName}
                  />
                  <label
                    form="text"
                    style={{ textAlign: "left", fontWeight: "bold" }}
                  >
                    Last name
                  </label>
                  <input
                    type="text"
                    name="last name"
                    minLength={2}
                    maxLength={30}
                    onChange={this.handleChangeLastName}
                  />
                  <label
                    form="email"
                    htmlFor="email"
                    style={{ textAlign: "left", fontWeight: "bold" }}
                  >
                    Email
                  </label>
                  <input
                    type="email"
                    name="Email Address"
                    onChange={this.handleChangeEmail}
                    minLength={5}
                    maxLength={40}
                  />
                  <label form="Message">Your slogan for the race</label>
                  <textarea name="message" onChange={this.handleChangeSlogan} />
                  <Button variant="success" onClick={() => this.handleSubmit()}>
                    Submit
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Sponsoring;
