import React, { Component } from "react";
import PhotoItem from "./Photos";
import { fetchPhotos } from "../api/api.js";

class Photos extends Component {
  state = {
    photos: [],
    isLoadingPhotos: true
  };

  componentDidMount() {
    this.searchPhotos();
  }

  componentWillUnmount(){
    console.log('unmount')
  }

  searchPhotos = (tags = "") => {
    console.log('here')
    const tagsParam = tags.replace(" ", ",");
    fetchPhotos(tagsParam)
      .then(photos => {
        this.setState({
          photos,
          isLoadingPhotos: false
        });
      })
      .catch((e) => {
      this.setState({
        isLoadingPhotos: false
      });
      alert("Could not retrieve photos. Please check your credentials.");
    })
  };

  renderPhotos() {

  }
  render() {
    console.log('this.state.photos', this.state.photos)
    const [first] = this.state.photos
    if(first){
      return <PhotoItem photo={first} />
    }
    return <div/>
    return this.state.photos.map((photo, i) => {
      return (
        <div key={`photo_${i}`} className="column is-one-quarter">
          <PhotoItem photo={photo} />
        </div>
      );
    });
  }
}

export default Photos;
