import React, { Component } from "react";
import PhotoItem from "./PhotoItem";
import { fetchPhotos } from "../api/api.js";
import { Container, Row, Col } from "react-bootstrap";

const PADDING_SCROLL = 300;

class Photos extends Component {
  constructor(props) {
    super(props);
    this.state = {
      photos: [],
      isLoadingPhotos: true,
      hasReached: false
    };
    this.containerRef = null;
    this.setContainerRef = element => {
      this.containerRef = element;
    };
  }

  componentDidMount() {
    this.searchPhotos();

  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.onScroll, false);
  }

  searchPhotos = (tags = "bikerace") => {
    const tagsParam = tags.replace("", ",");
    fetchPhotos(tagsParam)
      .then(photos => {
        this.setState({
          photos,
          isLoadingPhotos: false
        });
      })
      .catch(e => {
        this.setState({
          isLoadingPhotos: false
        });
        alert("Could not retrieve photos. Please check your credentials.");
      });
  };

  onScroll = () => {
    const hasReached =
      this.containerRef &&
      window.scrollY + window.innerHeight >=
        this.containerRef.offsetHeight - PADDING_SCROLL;
    return !this.state.hasReached && hasReached && this.setState({ hasReached })
  };

  render() {
    return (

      <Container style={{ marginTop: "30px" }}>
        <Row>
          {console.log('larouq', this.state.hasReached)}
          {this.state.photos.map(photo => {
            return (
              <Col xs={3} style={{ marginBottom: "10px" }} key={photo.id}>
                <div className="column is-one-quarter">
                  <PhotoItem photo={photo} />
                </div>
              </Col>
            );
          })}
        </Row>
      </Container>
    );
  }
}

export default Photos;
