import React, { Component } from "react";
import PhotoItem from "./PhotoItem";
import { fetchPhotos } from "../api/api.js";
import { Card, Container, Row, Col } from "react-bootstrap";
import { CONNREFUSED } from "dns";

class Photos extends Component {
  state = {
    photos: [],
    isLoadingPhotos: true
  };

  componentDidMount() {
    this.searchPhotos();
  }

  searchPhotos = (tags = "bikerace") => {
    const tagsParam = tags.replace("", ",");
    fetchPhotos(tagsParam)
      .then(photos => {
        this.setState({
          photos,
          isLoadingPhotos: false
        });
      })
      .catch(e => {
        this.setState({
          isLoadingPhotos: false
        });
        alert("Could not retrieve photos. Please check your credentials.");
      });
  };

  render() {
    // return this.state.photos.map((photo, i) => {
    //   return (
    //       <div key={`photo_${i}`} className="column is-one-quarter">
    //         <PhotoItem photo={photo} />
    //       </div>
    //   );
    // });
    return (
      <Container>
        <Row>
          {this.state.photos.map((photo, i) => {
            return (
              <Col>
                <div key={`photo_${i}`} className="column is-one-quarter">
                  <PhotoItem photo={photo} />
                </div>
              </Col>
            );
          })}
        </Row>
      </Container>
    );
  }
}

export default Photos;
