import React, { Component } from 'react';
import {Link} from "react-router-dom";
import { slide as Menu } from 'react-burger-menu'


class Header extends Component {
  constructor(props) {
  super(props)
    this.state = {
      color : 'black',
      displayBurger: true,
    }
  } 

  listenScrollEvent = e => {
    if (window.scrollY > 50) {
      this.setState({color: 'rgba(0, 0, 0, 0.6)'})
    } 
    else {
      this.setState({color: 'black'})
    }
  }

  componentDidMount() {
    window.addEventListener('scroll', this.listenScrollEvent)
    window.addEventListener('resize', this.listenResizeEvent)
  }

  listenResizeEvent = e => {
    if(window.innerWidth < 1024) {
      this.setState({displayBurger: false})
    }
    else {
      this.setState({displayBurger: true})
    }
  }

  render(){
    const isMenuOpen = function(state) {
      return state.isOpen;
    };

    return(
      <header className="App-header" style={{background: this.state.color}}>
        <nav>
          <Link style={{textDecoration: 'none'}} to="/">
              <h2 className="name">Benoit Rouqui√©</h2>
          </Link>
          {
            this.state.displayBurger 
            ? (
            <div className="navbar">
              <Link className="navlink" to="/">Home</Link>
              <Link className="navlink" to="/about">About</Link>
              <Link className="navlink" to="/contact">Contact</Link>
              <p className="bar" style={{color: 'white', marginRight: '10px', marginLeft: '10px'}}>|</p>
            </div>
          )
          : (
            <Menu className="bm-menu-wrap" right onStateChange={isMenuOpen} >
              <Link clasclassNames="menu-item" to="/">Home</Link>
              <Link className="menu-item" to="/about">About</Link>
              <Link className="menu-item" to="/contact">Contact</Link>
            </Menu>
          )
          }
        </nav>
      </header>
    )
  }
}

export default Header;
