import React, { Component } from "react";
import { Card, Container, Row, Col } from "react-bootstrap";
import biketourcover from "../assets/biketourcover.jpg";
import "./Home.scss";
import "bootstrap/dist/css/bootstrap.css";
import moment from "moment";

class Home extends Component {
  render() {
    const eventTime = 1366549200; // Timestamp - Sun, 21 Apr 2013 13:00:00 GMT
    const currentTime = moment(); // Timestamp - Sun, 21 Apr 2013 12:30:00 GMT
    const diffTime = eventTime - currentTime;
    const duration = moment.duration(diffTime * 1000, "milliseconds");
    const interval = 1000;

    setInterval(() => {
      duration = moment.duration(duration - interval, "milliseconds");
      (".countdown").text(
        duration.hours() + ":" + duration.minutes() + ":" + duration.seconds()
      );
    }, interval);


    return (
      <Container>
        <Row>
          <Col>
            <Card>
              <Card.Img variant={"top"} src={`${biketourcover}`} />
              <Card.Body>
                <Card.Text>
                  Trainings have started for Boulder Bike Tour 2020 ! Credit:
                  Marcus Spiske
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row style={{ marginTop: "5px" }}>
          <Col xs={4}>
            <Card style={{ height: "100%" }}>
              <Card.Body>
                <Card.Title>Running & Sleep</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  March 6, 2018 by Franck
                </Card.Subtitle>
                <Card.Text>
                  In addition to training hard and eating right, it’s equally
                  important to personalize your sleep environment...
                </Card.Text>
                <Card.Link href="#">Read More</Card.Link>
              </Card.Body>
            </Card>
          </Col>
          <Col xs={8}>
            <Card bg="dark" text="white" style={{ height: "100%" }}>
              <Card.Header>Are you ready ?</Card.Header>
              <Card.Body>
                <Card.Title>The course will begin in:</Card.Title>
                <Card.Text>
                  temere
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Home;
