import React, { Component } from "react";
import ReactMapboxGl, { Layer, Marker } from "react-mapbox-gl";
import {fetchRiders} from '../api/backendApi'

// ES5

const Map = ReactMapboxGl({
  accessToken:
    "pk.eyJ1IjoibGFyb3VxIiwiYSI6ImNqdWkydHVlNDBkajE0Mm43amViYTc5cmMifQ.wORRPu3OalU4jRYfUBJ01A"
});

class Location extends Component {
  constructor(props){
    super(props)
    this.state = {
      riders: []
    }
  }

  async componentDidMount() {
    const riders = await fetchRiders()
    this.setState({riders})
  }
  render() {

    return (
      <Map
        style={"mapbox://styles/mapbox/streets-v8"}
        containerStyle={{
          height: "100vh",
          width: "100vw"
        }}
        center={[-105.2705456, 40.0149856]}
        zoom={[11]}
      >
        <Layer
          type="symbol"
          id="marker"
          layout={{ "icon-image": "marker-15" }}
        />

        {this.state.riders && this.state.riders.map((rider, index) => {
          return <Marker latitude={rider.lat} longitude={rider.lng}>{rider.first_name}</Marker>
        })}
      </Map>
    );
  }
}

export default Location;
