import axios from "axios";

const BACKEND_API_URL = "http://localhost:3000";

export const startHerokuVM = () => async dispatch => {
  await axios.get();
};

export const fetchRiders = () => async dispatch => {
  try {
    const res = await axios.get(`${BACKEND_API_URL}/riders`);
    if (res.status === 200) {
      return dispatch({ type: fetchRider, riders: res.data });
    }
  } catch ({ response }) {
    new Error('backend api failed')
  }
};

export const fetchRider = id => async dispatch => {
    try {
      const res = await axios.get(`${BACKEND_API_URL}/riders/${id}`);
      dispatch({
        type: fetchRider,
        rider: res.data[0]
      });
    } catch ({ response }) {
        new Error('backend api failed')
    }
  };

export default {
  fetchRiders, startHerokuVM
};
