import React, { Component } from "react";
import PhotoItem from "./PhotoItem";
import { fetchPhotos } from "../api/api.js";
import { Container, Row, Col } from "react-bootstrap";

class Photos extends Component {
  state = {
    photos: [],
    isLoadingPhotos: true
  };

  componentDidMount() {
    this.searchPhotos();
  }


  searchPhotos = (tags = "bikerace") => {
    const tagsParam = tags.replace("", ",");
    fetchPhotos(tagsParam)
      .then(photos => {
        this.setState({
          photos,
          isLoadingPhotos: false
        });
      })
      .catch(e => {
        this.setState({
          isLoadingPhotos: false
        });
        alert("Could not retrieve photos. Please check your credentials.");
      });
  };

  handleScroll = event => {
    let element = event.target;
    if (element.scrollHeight - element.scrollTop === element.clientHeight) {
      // do something at end of scroll
      console.log("here");
    }
  };

  render() {
    return (
      <Container onScroll={this.handleScroll} style={{ marginTop: "30px" }}>
        <Row>
          {this.state.photos.map(photo => {
            return (
              <Col xs={3} style={{ marginBottom: "10px" }} key={photo.id}>
                <div className="column is-one-quarter">
                  <PhotoItem photo={photo} />
                </div>
              </Col>
            );
          })}
        </Row>
      </Container>
    );
  }
}

export default Photos;
