import React, { Component } from "react";
import ReactMapboxGl, { Popup, Marker } from "react-mapbox-gl";
import { fetchRiders } from "../api/backendApi";
import "./Location.scss";

// ES5

const Map = ReactMapboxGl({
  accessToken:
    "pk.eyJ1IjoibGFyb3VxIiwiYSI6ImNqdWkydHVlNDBkajE0Mm43amViYTc5cmMifQ.wORRPu3OalU4jRYfUBJ01A"
});

class Location extends Component {
  constructor(props) {
    super(props);
    this.state = {
      riders: [],
      riderMarker: false

    };
  }

  async componentDidMount() {
    const riders = await fetchRiders();
    this.setState({ riders });
  }

  handleClick = () => {
    this.setState({riderMarker: true})
  }
  render() {
    return (
      <Map
        style={"mapbox://styles/mapbox/streets-v8"}
        containerStyle={{
          height: "100vh",
          width: "100vw"
        }}
        center={[-105.2705456, 40.0149856]}
        zoom={[12]}
      >
        {this.state.riders &&
          this.state.riders.map(rider => {
            return (
              <Popup
                coordinates={[rider.lng, rider.lat]}
                key={rider.id}
                offset={{
                  "bottom-left": [12, -38],
                  bottom: [0, -38],
                  "bottom-right": [-12, -38]
                }}
                onClick={() =>
                  this.handleClick(rider.lng, rider.lat, rider.avatar)
                }
              >
                <p style={{ fontSize: "20px" }}>{rider.first_name}</p>
                <img
                  src={rider.avatar}
                  style={{ borderRadius: "50%", height: "50px" }}
                />
              </Popup>
            );
          })}
      </Map>
    );
  }
}

export default Location;
